import React from 'react';
import PropTypes from 'prop-types';

const SelectChannel = ({ onChange }) => {
  const channelsArray = ['itv', 'itv2', 'itv3', 'itv4', 'itvbe', 'citv'];

  return (
    <form className=''>
      <section className='select-channel'>
        <label htmlFor='select-channel__control' />
        <select className='form-control' onChange={onChange} id={'select-channel__control'}>
          {channelsArray.map((value, i) => {
            return (
              <option value={value} key={value + i}>
                {value}
              </option>
            );
          })}
        </select>
      </section>
    </form>
  );
};

SelectChannel.propTypes = {
  channels: PropTypes.array,
  categories: PropTypes.array
};

export default SelectChannel;
