import React from 'react';
import PropTypes from 'prop-types';
import moment from 'moment';

const ChannelsInfo = ({ categories, channels }) => {
  console.log(channels);
  return (
    <section className='channel-selection'>
      {channels === '' && <h3>Please select a channel.</h3>}
      {channels &&
        categories.map((programme, i) => {
          const programmeEmbedded = programme._embedded.latestProduction;
          const episodes = programmeEmbedded.episode ? programmeEmbedded.episode : 'N/A';

          return (
            <div key={programme.id} className='row channel-selection__wrapper'>
              <img className='col-md-4' src={programmeEmbedded._links.image.href} />
              <div className='col-md-8 channel-selection__info'>
                <h2 className='channel-selection__info-title'>{programme.title}</h2>
                <p className='channel-selection__info-synopses'>{programme.synopses.ninety}</p>
                <p className='channel-selection__info-duration'>
                  <strong>Duration:</strong>{' '}
                  <span className='channel-selection__info-brodcast-duration' key>
                    {programmeEmbedded.duration.display}
                  </span>
                </p>

                <p className='channel-selection__info-category'>
                  <strong>Category:</strong>{' '}
                  {programmeEmbedded._embedded.categories.map((category, i) => (
                    <span className='channel-selection__info-category-name' key={category.name + i}>
                      {category.name}
                      {' - '}
                    </span>
                  ))}
                </p>

                <p className='channel-selection__info-brodcast'>
                  <strong>Broadcast day:</strong>{' '}
                  <span className='channel-selection__info-brodcast-day'>
                    {moment(programmeEmbedded.broadcastDateTime.commissioning).format('DD/M/YYYY')}
                  </span>
                </p>

                <p className='channel-selection__info-brodcast'>
                  <strong>Broadcast time:</strong>{' '}
                  <span className='channel-selection__info-brodcast-time'>
                    {moment(programmeEmbedded.broadcastDateTime.commissioning).format('LT')}
                  </span>
                </p>

                <p className='channel-selection__info-episodes'>
                  <strong>Episode number:</strong>{' '}
                  <span className='channel-selection__info-episodes-number'>{episodes}</span>
                </p>

                <p className='channel-selection__info-channel'>
                  <strong>Channel:</strong>{' '}
                  <span className='channel-selection__info-channel-name'>
                    {programmeEmbedded._embedded.channel.name} -
                  </span>
                  <span className='channel-selection__info-channel-strapline'>
                    {programmeEmbedded._embedded.channel.strapline}
                  </span>
                </p>
              </div>
            </div>
          );
        })}
    </section>
  );
};

ChannelsInfo.propTypes = {
  channels: PropTypes.string,
  categories: PropTypes.array
};

export default ChannelsInfo;
